package BasicProgram;

public class ASIICvalue {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		char ch1='d';
		char ch2='D';
		int asci1=ch1;
		int asci2=ch2;
		
		System.out.println("ASCI Value 1 :" + asci1);
		System.out.println("ASCI Value 2 :" + asci2);
		
		//Converting number to char
		int number=100;
		char asci3=(char) number;
		System.out.println("ASCI Value 3 :" + asci3);

	}

}
